import React, { Component } from "react";
import Viewicon from "../Assets/Icons/PNG/views.png";
import Likeicon from "../Assets/Icons/PNG/likes.png";
import axios from "axios";

class Maincontent extends Component {
  // console.log("PROPS IN MAINCONTENT", props);

  componentDidUpdate(prevProps, prevState) {
    console.log("prevprops", prevProps);
    console.log("thisprops", this.props);
    // console.log("UPDATE1", prevState.videoInfo.id);
    // console.log("UPDATE2", this.props.videoInfo.id);

    if (this.props) {
      if (prevProps) {
        console.log(
          "this.props.match.params.id: ",
          this.props.videoInfo.match.params.id
        );
        console.log("vs");
        console.log(this.props.videoInfo.video.id);

        console.log(
          "are they same?",
          this.props.videoInfo.match.params.id === this.props.videoInfo.video.id
        );

        if (
          this.props.videoInfo.match.params.id !== this.props.videoInfo.video.id
        )
          if (prevProps.videoInfo.length < 1) {
            return axios
              .get(
                `https://project-2-api.herokuapp.com/videos/${this.props.videoInfo.id}?api_key=bb1b5cb2-60cb-4779-bfb3-fa4acdb34abc`
              )
              .then(reponse => {
                this.setState({});
              });
            // console.log("this state", this.state);
            // console.log("UPDATE1", prevProps.videoInfo.id);
            // console.log("UPDATE2", this.props.videoInfo.id);

            // console.log("prevstate", prevState);
            // console.log("thisstate", this.props);
            // console.log("prevstate", prevState.videoInfo.length);
            console.log("DO THE INITIAL LOAD: ");
            return axios
              .get(
                `https://project-2-api.herokuapp.com/videos/1ainjruutd1j?api_key=bb1b5cb2-60cb-4779-bfb3-fa4acdb34abc`
              )
              .then(response => {
                console.log("response data: ", response.data);
                this.setState({ videoInfo: response.data });
              });
          } //is state and prestate exists run axios//
        // if (prevState.videoInfo.length > 0) {
        console.log("prevstate", prevState);
        console.log("thisstate", this.props);
        // console.log("prevstate", prevState.videoInfo.length);
        // console.log("UPDATE1", prevState.videoInfo.id);
        // console.log("UPDATE2", this.props.videoInfo.id);
        // if (prevState.videoInfo.id !== this.props.videoInfo.id) {
        //   return axios
        //     .get(
        //       `https://project-2-api.herokuapp.com/videos/${this.props.videoInfo.id}?api_key=bb1b5cb2-60cb-4779-bfb3-fa4acdb34abc`
        //     )
        //     .then(reponse => {
        //       this.setState({});
        //     });
        //   // }
        // }
      }
    }
  }

  render() {
    return (
      <div className="combination">
        <div className="maincontent__textcontainer">
          <div className="maincontent__title">{this.props.videoInfo.title}</div>
          <div className="maincontent__semicontainer">
            <div className="maincontent__author">
              <p>{this.props.videoInfo.channel}</p>{" "}
              <p className="maincontent__date">
                {this.props.videoInfo.timestamp}
              </p>
            </div>
            <div className="maincontent__icons">
              <div className="maincontent__views">
                <img className="maincontent__viewsicon" src={Viewicon} />
                <p className="maincontent__iconnumber">
                  {this.props.videoInfo.views}
                </p>
              </div>
              <div className="maincontent__likes">
                <img className="maincontent__likesicon" src={Likeicon} />
                <p className="maincontent__iconnumber">
                  {this.props.videoInfo.likes}
                </p>
              </div>
            </div>
          </div>
          <p className="maincontent__text">
            {this.props.videoInfo.description}
          </p>
        </div>
      </div>
    );
  }
}

export default Maincontent;
