import React from "react";
import Mohan from "../Assets/Images/Mohan.jpg";

export default function commentArray(props) {
  let threeComments = [];
  for (let i = 0; i < props.commentArray.length; i++) {
    threeComments.push(
      <div className="comments">
        <h1>{props.commentArray[i].Name}</h1>
        <h2>{props.commentArray[i].Comment}</h2>
      </div>
    );
  }

  //console.log(props.commentArray[i]);
  // return (
  //   <>
  //     <ul className="comments">
  //       {props.commentArray[i].Name}
  //       {props.commentArray[i].Comment}
  //     </ul>
  //   </>
  // );

  return (
    <>
      <div className="comment__container">
        <div className="comment__title">
          <p>3 Comments</p>
        </div>
        <div className="comment__heading">
          <p>JOIN THE CONVERSATION</p>
        </div>
        <div className="comment__textboxcontainer">
          <div className="column">
            <img className="comment__textboxpic" src={Mohan} />
          </div>
          <textarea
            className="comment__textbox"
            placeholder="That was easily the most spectacular BMX moment ever."
            cols="30"
            rows="5"
          ></textarea>

          <div className="comment__buttoncontainer">
            <input
              className="comment__button"
              id="--button"
              type="button"
              name="BUTTON"
              value="COMMENT"
            />
          </div>
        </div>
        <div className="comment__threecomments">{threeComments}</div>
      </div>
    </>
  );
}
