import React, { Component } from "react";
import Upload from "./components/Upload";
import Home from "./components/Home";
import "./styles/style.css";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Redirect
} from "react-router-dom";

class App extends Component {
  state = {
    footerArray: [],
    videoInfo: [],
    commentArray: [],

    arr: []
  };

  render() {
    console.log("THIS STATE VIDEO INFO IN THE RENDER", this.state);
    return (
      <Router>
        <Switch>
          <Route
            path="/videos/:id"
            render={routerProps => (
              <Home
                state={this.state}
                video={this.state.videoInfo}
                {...routerProps}
              />
            )}
          />
          {/* <Redirect exact from="/" /> */}
          <Route path="/upload" component={Upload}></Route>
        </Switch>
      </Router>
    );
  }
}
export default App;
