import React, { Component } from "react";
import Viewicon from "../Assets/Icons/PNG/views.png";
import Likeicon from "../Assets/Icons/PNG/likes.png";
import Comments from "./Comments";
import Footer from "./Footer";
import axios from "axios";

class Maincontent extends Component {
  state = {
    footerArray: [],
    videoInfo: [],
    commentArray: [],

    arr: []
  };
  componentDidMount() {
    axios
      .all([
        axios.get(
          `https://project-2-api.herokuapp.com/videos?api_key=bb1b5cb2-60cb-4779-bfb3-fa4acdb34abc`
        ),
        axios.get(
          `https://project-2-api.herokuapp.com/videos/1ainjruutd1j?api_key=bb1b5cb2-60cb-4779-bfb3-fa4acdb34abc`
        )
      ])
      .then(responseArray => {
        // console.log(responseArray[0]);
        // console.log(responseArray[1]);
