        if (
          this.props.videoInfo.match.params.id === this.props.videoInfo.video.id
        ) {
          return await axios
            .get(
              `https://project-2-api.herokuapp.com/videos/${this.props.videoInfo.match.params.id}?api_key=bb1b5cb2-60cb-4779-bfb3-fa4acdb34abc`
