import React, { Component } from "react";
import Header from "./components/Header";
import Hero from "./components/Hero";
import Comments from "./components/Comments";
import Footer from "./components/Footer";
import Maincontent from "./components/Maincontent";
import Upload from "./components/Upload";
import Home from "./components/Home";
import axios from "axios";
// import "./styles/style.css";
import {
  BrowserRouter as Router,
  Switch,
  Route,
  Link,
  Redirect
} from "react-router-dom";

class App extends Component {
  state = {
    footerArray: [],
    videoInfo: [],
    commentArray: [],

    arr: []
  };
  componentDidMount() {
    axios
      .all([
        axios.get(
          `https://project-2-api.herokuapp.com/videos?api_key=bb1b5cb2-60cb-4779-bfb3-fa4acdb34abc`
        ),
        axios.get(
          `https://project-2-api.herokuapp.com/videos/1ainjruutd1j?api_key=bb1b5cb2-60cb-4779-bfb3-fa4acdb34abc`
        )
      ])
      .then(responseArray => {
        // console.log(responseArray[0]);
        // console.log(responseArray[1]);
        this.setState({
          arr: responseArray[0].data,
          videoInfo: responseArray[1].data
        });
      });
  }

  fetchVideo = id => {
    // console.log("fetch vid", id);
    axios
      .get(
        `https://project-2-api.herokuapp.com/videos/${id}?api_key=bb1b5cb2-60cb-4779-bfb3-fa4acdb34abc`
      )
      .then(res => {
        // console.log(res.data);
        this.setState({
          videoInfo: res.data
        });
      });
  };

  componentDidUpdate(prevProps, prevState) {
    // console.log("prevprops", prevProps);
    // console.log("thisprops", this.props);
    // console.log("UPDATE1", prevState.videoInfo.id);
    // console.log("UPDATE2", this.state.videoInfo.id);

    if (this.state) {
      if (prevState) {
        console.log("UPDATE1", prevState.videoInfo.id);
        console.log("UPDATE2", this.state.videoInfo.id);

        // console.log("prevstate", prevState);
        // console.log("thisstate", this.state);
        // console.log("prevstate", prevState.videoInfo.length);
        if (prevState.videoInfo.length < 1) {
          console.log("DO THE INITIAL LOAD: ");
          return axios
            .get(
              `https://project-2-api.herokuapp.com/videos/1ainjruutd1j?api_key=bb1b5cb2-60cb-4779-bfb3-fa4acdb34abc`
            )
            .then(response => {
              console.log("response data: ", response.data);
              this.setState({ videoInfo: response.data });
            });
        } //is state and prestate exists run axios//
        // if (prevState.videoInfo.length > 0) {
        console.log("prevstate", prevState);
        console.log("thisstate", this.state);
        // console.log("prevstate", prevState.videoInfo.length);
        // console.log("UPDATE1", prevState.videoInfo.id);
        // console.log("UPDATE2", this.state.videoInfo.id);
        // if (prevState.videoInfo.id !== this.state.videoInfo.id) {
        //   return axios
        //     .get(
        //       `https://project-2-api.herokuapp.com/videos/${this.state.videoInfo.id}?api_key=bb1b5cb2-60cb-4779-bfb3-fa4acdb34abc`
        //     )
        //     .then(reponse => {
        //       this.setState({});
        //     });
        //   // }
        // }
      }
    }
  }
  // console.log("CONNOR", this.props.state.videoInfo);
  // // wrap handleVidChange in an if statement e.g. prevProps compared to currnt param.id
  // this.props.handleVidChange(this.props.match.params.id);
  // console.log("HELLO", this.props.match.params.id);
  // console.log("LETS GO", props);
  //   if (prevProps !== this.props.match.params.id)
  //     return axios
  //       .get(
  //         `https://project-2-api.herokuapp.com/videos/${this.state.videoInfo.id}?api_key=bb1b5cb2-60cb-4779-bfb3-fa4acdb34abc`
  //       )
  //       .then(reponse => {
  //         this.setState({});
  //       });
  // }

  render() {
    console.log("THIS STATE VIDEO INFO IN THE RENDER", this.state.videoInfo);
    return (
      <Router>
        <Header />

        <Switch>
          <Route
            path="/videos/:id"
            render={routerProps => (
              <Home
                state={this.state}
                video={this.state.videoInfo}
                handleVidChange={this.fetchVideo}
                {...routerProps}
              />
            )}
          />
          <Route
            path="/"
            exact
            render={routerProps => (
              <Home
                state={this.state}
                handleVidChange={this.fetchVideo}
                {...routerProps}
              />
            )}
            exact
          />
          {/* <Route path="/upload" component={Upload}></Route> */}
        </Switch>
      </Router>
    );
  }
}
export default App;
