import React, { Component } from "react";
import Header from "./Header";
import Hero from "./Hero";
import Comments from "./Comments";
import Footer from "./Footer";
import Maincontent from "./Maincontent";
import Upload from "./Upload";

class Home extends Component {
  // console.log("url param id", props.match.params.id);

  // make another axios get request using the video id from the url
  // this could be done in this component however you will need to make it a class based component
  // or you can try to call a method from App.js from here to fetch more videos

  componentDidUpdate(prevProps) {
    console.log("prevprops", prevProps);
    console.log("thisprops", this.props);
    // wrap handleVidChange in an if statement e.g. prevProps compared to currnt param.id
    this.props.handleVidChange(this.props.match.params.id);
  }

  render() {
    return (
      <>
        <Hero />
        <div className="container">
          <div className="combination">
            <Maincontent videoInfo={this.props.state.videoInfo} />

            <Comments commentArray={this.props.state.commentArray} />
          </div>
          <div className="combo">
            <Footer footerArray={this.props.state.arr} />
          </div>
        </div>
      </>
    );
  }
}

export default Home;
