import React, { Component } from "react";
import Viewicon from "../Assets/Icons/PNG/views.png";
import Likeicon from "../Assets/Icons/PNG/likes.png";
import axios from "axios";
import Footer from "./Footer";
import Comments from "./Comments";
import Header from "./Header";
import Hero from "./Hero";
import ImageInfo from ".ImageInfo";

class Maincontent extends Component {
  // console.log("PROPS IN MAINCONTENT", props);
  state = {};
  async componentDidMount() {
    console.log(
      "did the component mount? check the url: ",
      this.props.videoInfo.match.params.id
    );
    const theId = this.props.videoInfo.match.params.id;
    await axios
      .all([
        axios.get(
          `https://project-2-api.herokuapp.com/videos?api_key=bb1b5cb2-60cb-4779-bfb3-fa4acdb34abc`
        ),
        axios.get(
          `https://project-2-api.herokuapp.com/videos/${theId}?api_key=bb1b5cb2-60cb-4779-bfb3-fa4acdb34abc`
        )
      ])
      .then(responseArray => {
        console.log("RESPONSES:::::");
        console.log(responseArray[0].data);
        console.log(responseArray[1].data);
        this.setState({
          videos: responseArray[0].data,
          videoInfo: responseArray[1].data
        });
      });
  }

  // fetchVideo = id => {
  //   // console.log("fetch vid", id);
  //   axios
  //     .get(
  //       `https://project-2-api.herokuapp.com/videos/${id}?api_key=bb1b5cb2-60cb-4779-bfb3-fa4acdb34abc`
  //     )
  //     .then(res => {
  //       // console.log(res.data);
  //       this.setState({
  //         videoInfo: res.data
  //       });
  //     });
  // };

  async componentDidUpdate(prevProps, prevState) {
    console.log("IT UPDATED!00");
    // console.log("this.state = videos array", this.state.arr);

    // console.log("prevprops", prevProps);
    // console.log("thisprops", this.props);
    // console.log("UPDATE1", prevState.videoInfo.id);
    // console.log("UPDATE2", this.props.videoInfo.id);

    if (this.state) {
      console.log("the state exists", this.state);
      if (this.state.videoInfo) {
        console.log("the state video info exists!", this.state.videoInfo);
        console.log("the current video is:", this.state.videoInfo.id);

        if (this.state.videoInfo.id !== this.props.videoInfo.match.params.id) {
          const theId = this.props.videoInfo.match.params.id;
          await axios
            .all([
              axios.get(
                `https://project-2-api.herokuapp.com/videos?api_key=bb1b5cb2-60cb-4779-bfb3-fa4acdb34abc`
              ),
              axios.get(
                `https://project-2-api.herokuapp.com/videos/${theId}?api_key=bb1b5cb2-60cb-4779-bfb3-fa4acdb34abc`
              )
            ])
            .then(responseArray => {
              console.log("RESPONSES:::::");
              console.log(responseArray[0].data);
              console.log(responseArray[1].data);
              this.setState({
                videos: responseArray[0].data,
                videoInfo: responseArray[1].data
              });
            });
        }
        console.log(
          "the current video ID is: ",
          this.props.videoInfo.match.params.id
        );
      }
      if (this.state.videos) {
        console.log("the state videos exists!", this.state.videos);
      }
    }
  }
  render() {
    if (this.state.videoInfo) {
      // console.log("THIS STATE IN RENDER: ", this.state);
      return (
        <>
          <div>
            <Header videoInfo={this.state} />
            <Hero videoInfo={this.state} />
            <Comments comments={this.state.videoInfo.comments} />
            <Footer footerArray={this.state.videos} />
          </div>
          {/* <div className="combination">
            <div className="maincontent__textcontainer">
              <div className="maincontent__title">
                {this.state.videoInfo.title}
              </div>
              <div className="maincontent__semicontainer">
                <div className="maincontent__author">
                  <p>{this.state.videoInfo.channel}</p>
                  <p className="maincontent__date">
                    {this.state.videoInfo.timestamp}
                  </p>
                </div>
                <div className="maincontent__icons">
                  <div className="maincontent__views">
                    <img
                      className="maincontent__viewsicon"
                      alt="view"
                      src={Viewicon}
                    />
                    <p className="maincontent__iconnumber">
                      {this.state.videoInfo.views}
                    </p>
                  </div>
                  <div className="maincontent__likes">
                    <img
                      className="maincontent__likesicon"
                      alt="likes"
                      src={Likeicon}
                    />
                    <p className="maincontent__iconnumber">
                      {this.state.videoInfo.likes}
                    </p>
                  </div>
                </div>
              </div>
              <p className="maincontent__text">
                {this.state.videoInfo.description}
              </p>
              <Comments comments={this.state.videoInfo.comments} />
              <Footer footerArray={this.state.videos} />
            </div>
          </div> */}
        </>
      );
    } else {
      return <></>;
    }
  }
}

export default Maincontent;
