import React, { Component } from "react";
// import Header from "./Header";
import Hero from "./Hero";
import Comments from "./Comments";
import Footer from "./Footer";
import Maincontent from "./Maincontent";
// import Upload from "./Upload";
class Home extends Component {
  componentDidMount() {
    console.log(
      "did the component mount? check the url: ",
      this.props.videoInfo.match.params.id
    );
    const theId = this.props.videoInfo.match.params.id;
    axios
      .all([
        axios.get(
          `https://project-2-api.herokuapp.com/videos?api_key=bb1b5cb2-60cb-4779-bfb3-fa4acdb34abc`
        ),
        axios.get(
          `https://project-2-api.herokuapp.com/videos/${theId}?api_key=bb1b5cb2-60cb-4779-bfb3-fa4acdb34abc`
        )
      ])
      .then(responseArray => {
        console.log("RESPONSES:::::");
        console.log(responseArray[0].data);
        console.log(responseArray[1].data);
        this.setState({
          arr: responseArray[0].data,
          videoInfo: responseArray[1].data
        });
      });
  }

  // fetchVideo = id => {
  //   // console.log("fetch vid", id);
  //   axios
  //     .get(
  //       `https://project-2-api.herokuapp.com/videos/${id}?api_key=bb1b5cb2-60cb-4779-bfb3-fa4acdb34abc`
  //     )
  //     .then(res => {
  //       // console.log(res.data);
  //       this.setState({
  //         videoInfo: res.data
  //       });
  //     });
  // };

  async componentDidUpdate(prevProps, prevState) {
    console.log("prevprops", prevProps);
    console.log("thisprops", this.props);
    // console.log("UPDATE1", prevState.videoInfo.id);
    // console.log("UPDATE2", this.props.videoInfo.id);

    if (this.props) {
      if (prevProps) {
        console.log(
          "this.props.match.params.id: ",
          this.props.videoInfo.match.params.id
        );
        console.log("vs");
        console.log(this.props.videoInfo.video.id);

        console.log(
          "are they same?",
          this.props.videoInfo.match.params.id === this.props.videoInfo.video.id
        );

        if (
          this.props.videoInfo.match.params.id === this.props.videoInfo.video.id
        ) {
          return await axios
            .get(
              `https://project-2-api.herokuapp.com/videos/${this.props.videoInfo.match.params.id}?api_key=bb1b5cb2-60cb-4779-bfb3-fa4acdb34abc`
            )
            .then(response => {
              console.log("response data: ", response.data);
              this.setState({ videoInfo: response.data });
            });

          console.log("SHOULD STOP");
          // console.log("this state", this.state);
          // console.log("UPDATE1", prevProps.videoInfo.id);
          // console.log("UPDATE2", this.props.videoInfo.id);

          // console.log("prevstate", prevState);
          // console.log("thisstate", this.props);
          // console.log("prevstate", prevState.videoInfo.length);
          // console.log("DO THE INITIAL LOAD: ");
        } //is state and prestate exists run axios//
        // if (prevState.videoInfo.length > 0) {
        // console.log("prevstate", prevState);
        // console.log("thisstate", this.props);
        // console.log("prevstate", prevState.videoInfo.length);
        // console.log("UPDATE1", prevState.videoInfo.id);
        // console.log("UPDATE2", this.props.videoInfo.id);
        // if (prevState.videoInfo.id !== this.props.videoInfo.id) {
        //   return axios
        //     .get(
        //       `https://project-2-api.herokuapp.com/videos/${this.props.videoInfo.id}?api_key=bb1b5cb2-60cb-4779-bfb3-fa4acdb34abc`
        //     )
        //     .then(reponse => {
        //       this.setState({});
        //     });
        //   // }
        // }
      }
    }
  }
  // console.log("url param id", props.match.params.id);

  // make another axios get request using the video id from the url
  // this could be done in this component however you will need to make it a class based component
  // or you can try to call a method from App.js from here to fetch more videos

  // compare prev props current(this) props
  // if they aren't the same do a thang
  // if not the same then does axios fetch for new ID

  render() {
    console.log("THIS.PROPS.VIDEOINFO0", props);

    return (
      <>
        <Hero />
        <div className="container">
          <div className="combination">
            <Maincontent videoInfo={props} />

            <Comments commentArray={props.state.commentArray} />
          </div>
          <div className="combo">
            <Footer footerArray={props.state.arr} />
          </div>
        </div>
      </>
    );
  }
}

export default Home;
